import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('https://v2.api.noroff.dev/online-shop')
      .then(response => {
        console.log('API Response:', response.data);  // Log the response
        setProducts(response.data.data);  // Access the products within the `data` field
      })
      .catch(error => {
        console.error('There was an error fetching the products!', error);
        setError(error.message);
      });
  }, []);

  const defaultImage = 'https://via.placeholder.com/150';

  return (
    <div>
      <h1>Product List</h1>
      {error && <p>Error: {error}</p>}
      <ul>
        {products.length > 0 ? (
          products.map(product => (
            <li key={product.id}>
              <Link to={`/product/${product.id}`}>
                <img 
                  src={product.image && product.image.url ? product.image.url : defaultImage} 
                  alt={product.name} 
                  className="product-image" 
                  onError={(e) => e.target.src = defaultImage}  // Fallback in case the image URL is invalid
                />
                <div>
                  <h2>{product.title}</h2>
                  <p>${product.price}</p>
                </div>
              </Link>
            </li>
          ))
        ) : (
          <p>No products available</p>
        )}
      </ul>
    </div>
  );
};

export default Home;
